templates.template:
#!/usr/bin/perl -w
#===============================================================================
#
#  FILE:        ${filename}
#
#  USAGE:       ./${filename} ${options}
#
#  DESCRIPTION: ${description}
#
# OPTIONS:      ${options}
# REQUIREMENTS: ---
# BUGS:         ---
# NOTES:        ---
# AUTHOR:       ${user}
# COMPANY:      ---
# VERSION:      1.0
# CREATED:      ${date} ${time} CET
# REVISION:     ---
#===============================================================================

use strict;
use warnings;

SWITCH: {
    (${cursor}) && do { ; last SWITCH};
    // && do { ; last SWITCH};
}

open(IN, "${Filename}") or die "could not open ${Filename}\n";
while(<IN>)
{
    ${cursor}
}
close IN;

open(IN, "${Program} |") or die "could not execute ${Program}\n";
while(<IN>)
{
    ${cursor}
}
close IN;

open(OUT, "> ${Filename}") or die "could not write ${Filename}\n";
${cursor}
close OUT;

open(OUT, "| ${Program}") or die "could not execute ${Program}\n";
${cursor}
close OUT;

open(IN, "< ${Inputfilename}") 
  or die "could not open ${Inputfilename}\n";
open(OUT, "> ${Outputfilename}") 
  or die "could not write ${Outputfilename}\n";
while(<IN>)
{
    ${cursor}
    print OUT $$_;
}
close OUT; close IN;

while(<>)
{
    ${cursor}
    print;

    if (eof) {
         # each individual file end, filename in $$ARGV
    }
}

#===================================================================
sub workOnFile
{
    if ( -d _ &&  /^public/) {
	$$File::Find::prune = 1;
	return;
    }
    # print "Entering: $$File::Find::name\n" if -d _;
    return unless /\.(bak|sik|ttt)$$/;
    print "$$File::Find::name\n";
    return;
}

#===================================================================
# ARGV contains toplevel dirs
@ARGV = ( '.' ) unless (scalar @ARGV);
find(\&workOnFile, @ARGV);

#===================================================================
sub workOnFile {
    print "Done: $$File::Find::name\n" if -d _;
    return unless -f _;
    print "$$File::Find::name\n";
    return;
}

#===================================================================
# ARGV contains toplevel dirs
@ARGV = ( '.' ) unless (scalar @ARGV);
finddepth(\&workOnFile, @ARGV);

open(IN, "< ${Inputfilename}") 
  or die "could not open ${Inputfilename}\n";
open(OUT, "> ${Outputfilename}") 
  or die "could not write ${Outputfilename}\n";
while(<IN>)
{
    ${cursor}
    print OUT $$_;
}
close OUT; close IN;
